const lastBin = flow.get('lastOpenedBin');
if (!lastBin || !lastBin.bin_id) {
    node.error('No bin is currently open. Please open a bin first.');
    node.status({fill:'red',shape:'ring',text:'No bin open'});
    return null;
}

const bin_id = lastBin.bin_id;
const user_qr = lastBin.user_qr_code || 'SB-user-001';
const materials = ['plastic', 'paper', 'glass', 'metal', 'organic', 'other'];
const material = materials[Math.floor(Math.random() * materials.length)];
const confidence = (Math.random() * 0.14 + 0.85).toFixed(2);

msg.topic = `bin/${bin_id}/detected`;
msg.payload = {
    bin_id: bin_id,
    user_qr_code: user_qr,
    material: material,
    material_type: material,
    confidence: parseFloat(confidence),
    timestamp: new Date().toISOString(),
    event: 'material_detected'
};

node.status({fill:'green',shape:'dot',text:`Trash detected in ${bin_id.substring(0,8)}`});
return msg;

const bin_id = msg.payload.bin_id || msg.topic.split('/')[1];
const user_qr = msg.payload.user_qr_code || 'SB-user-001';
flow.set('lastOpenedBin', {bin_id: bin_id, user_qr_code: user_qr, opened_at: new Date().toISOString()});
node.status({fill:'blue',shape:'dot',text:`Bin ${bin_id.substring(0,8)} opened`});
return null;

const bin_id = msg.payload.bin_id || msg.topic.split('/')[1];
flow.set('lastOpenedBin', null);
node.status({fill:'grey',shape:'ring',text:`Bin ${bin_id.substring(0,8)} closed`});
return null;
