const binData = msg.payload;

// Store the bin user selected in the app
flow.set('selectedBin', {
    bin_id: binData.bin_id,
    bin_name: binData.bin_name,
    nfc_tag_id: binData.nfc_tag_id,
    selected_at: new Date().toISOString()
});

// Clear previous scan
flow.set('nfcScanResult', null);

node.status({
    fill: 'blue',
    shape: 'dot',
    text: `User selected: ${binData.bin_name}`
});

msg.payload = {
    success: true,
    message: `Bin selected. Now scan NFC tag from Node-RED.`
};
msg.statusCode = 200;

return msg;

// Get the bin user selected in the app
const selectedBin = flow.get('selectedBin');

if (!selectedBin) {
    node.error('No bin selected! User must click "Use NFC" in the app first.');
    node.status({fill:'red', shape:'ring', text:'No bin selected'});
    return null;
}

// Simulate: User taps their phone on the bin's NFC tag
// In real life, phone reads the NFC chip on the bin
const scannedNfcTag = selectedBin.nfc_tag_id;

// Verify: Does scanned NFC match the selected bin?
if (scannedNfcTag === selectedBin.nfc_tag_id) {
    // SUCCESS: User is near the correct bin!
    const scanResult = {
        success: true,
        verified: true,
        bin_id: selectedBin.bin_id,
        bin_name: selectedBin.bin_name,
        nfc_tag_id: scannedNfcTag,
        scanned_at: new Date().toISOString()
    };

    flow.set('nfcScanResult', scanResult);

    node.status({
        fill: 'green',
        shape: 'dot',
        text: `✓ NFC Verified: ${selectedBin.bin_name}`
    });

    // Call backend API to open bin
    msg.url = `http://bin_service:8000/api/bins/list/${selectedBin.bin_id}/open/`;
    msg.method = 'POST';
    msg.payload = {
        user_qr_code: 'SB-user-001',
        nfc_tag_id: scannedNfcTag
    };

    return msg;
} else {
    // FAIL: Wrong bin!
    node.error('NFC verification failed! User is near wrong bin.');
    node.status({fill:'red', shape:'ring', text:'Wrong bin!'});
    return null;
}

// Store opened bin info for trash detection
const selectedBin = flow.get('selectedBin');
if (selectedBin && msg.statusCode === 200) {
    flow.set('lastOpenedBin', {
        bin_id: selectedBin.bin_id,
        user_qr_code: 'SB-user-001',
        opened_at: new Date().toISOString()
    });
    node.status({fill:'green', shape:'dot', text:'✓ Bin opened & stored'});
}
return null;

const binId = msg.req.params.bin_id;
const scanResult = flow.get('nfcScanResult');

if (scanResult && scanResult.bin_id === binId && scanResult.verified) {
    // Clear result after reading
    flow.set('nfcScanResult', null);

    msg.payload = scanResult;
    msg.statusCode = 200;
} else {
    msg.payload = {
        success: false,
        verified: false,
        message: 'No scan result yet'
    };
    msg.statusCode = 404;
}

return msg;

const lastBin = flow.get('lastOpenedBin');
if (!lastBin || !lastBin.bin_id) {
    node.error('No bin is currently open. Please open a bin first.');
    node.status({fill:'red',shape:'ring',text:'No bin open'});
    return null;
}

const bin_id = lastBin.bin_id;
const user_qr = lastBin.user_qr_code || 'SB-user-001';
const materials = ['plastic', 'paper', 'glass', 'metal', 'organic', 'other'];
const material = materials[Math.floor(Math.random() * materials.length)];
const confidence = (Math.random() * 0.14 + 0.85).toFixed(2);

msg.topic = `bin/${bin_id}/detected`;
msg.payload = {
    bin_id: bin_id,
    user_qr_code: user_qr,
    material: material,
    material_type: material,
    confidence: parseFloat(confidence),
    timestamp: new Date().toISOString(),
    event: 'material_detected'
};

node.status({fill:'green',shape:'dot',text:`Trash detected`});
return msg;

const bin_id = msg.payload.bin_id || msg.topic.split('/')[1];
const user_qr = msg.payload.user_qr_code || 'SB-user-001';
flow.set('lastOpenedBin', {bin_id: bin_id, user_qr_code: user_qr, opened_at: new Date().toISOString()});
node.status({fill:'green',shape:'dot',text:`✓ Bin opened`});
return null;

const bin_id = msg.payload.bin_id || msg.topic.split('/')[1];
flow.set('lastOpenedBin', null);
node.status({fill:'grey',shape:'ring',text:`Bin closed`});
return null;
